+<template>
+  <!-- Your template code here -->
+</template>
+
+<script setup lang="ts">
+import * as XLSX from 'xlsx';
+
+
+
+interface Question {
+  id: number;
+  text: string;
+  options: string[];
+  answer: string;
+}
+
+const questions: Question[] = [];
+
+
+/**
+ * 把文件按照二进制进行读取
+ * @param file 
+ * @returns 
+ */
+ const readFile=(file:File)=>{
+    return new Promise(resolve=>{
+        let reader = new FileReader();
+        reader.readAsBinaryString(file);
+        reader.onload = ev =>{
+            resolve(ev.target?.result);
+        }
+    });
+}
+
+/**
+ * 根据sheet索引读取Excel文件中的数据
+ /**@param file 文件
+ /** @param sheetIndex excel文件中sheet表索引 默认是从0开始
+ /**@returns  将表中的数据以json形式的数据返回
+*/
+const readExcelFile=async (file: File, sheetIndex: number)=>{
+  let data = await readFile(file);
+  let workbook = XLSX.read(data, { type: 'binary' });
+  let worksheet = workbook.Sheets[workbook.SheetNames[sheetIndex]];
+  data = xlsx.utils.sheet_to_json(worksheet);
+  console.log(data);
+  return data;
+}
+
+
+
+
+// 读取 Excel 文件
+const fileReader = new FileReader();
+
+fileReader.onload = (event: ProgressEvent<FileReader>) => {
+  const data = event.target?.result as string;
+  const workbook = XLSX.read(data, { type: 'binary' });
+
+  // 获取第一个工作表的数据
+  const worksheet = workbook.Sheets[workbook.SheetNames[0]];
+  const sheetData = XLSX.utils.sheet_to_json(worksheet);
+
+  // 将每一行数据转化为题目对象，并添加到数组中
+  sheetData.forEach((row: any) => {
+    const question: Question = {
+      id: row.id as number, // 题目 ID
+      text: row.text as string, // 题目内容
+      options: [row.option1, row.option2, row.option3, row.option4].filter(Boolean) as string[], // 选项列表
+      answer: row.answer as string, // 正确答案
+    };
+    questions.push(question);
+  });
+
+  console.log('测试题导入完成！');
+  console.log(questions); // 打印测试题数组
+};
+
+// 选择文件并读取数据
+const inputElement = document.createElement('input');
+inputElement.type = 'file';
+inputElement.accept = '.xlsx, .xls, .csv';
+inputElement.addEventListener('change', () => {
+  const file = (inputElement.files as FileList)[0];
+  fileReader.readAsBinaryString(file);
+});
+inputElement.click();
+
+
+// 触发文件读取
+fileReader.readAsBinaryString(excelFile);
+</script>
